import React from 'react';
import {BrowserRouter , Switch, Route, Link, Redirect, withRouter, useHistory }from "react-router-dom";
import './NavBar.css';
import SideDrawerButton from '../SideDrawer/SideDrawerButton';

var userInfoArray: [];
var user;


function getUserInfo() {
  Axios.post('http://localhost:3001/users', {
            username: user
        }).then((response) => {
          if(response){
            this.setState({
              usersInfoArray: response.data, // Store the information in the users info array
            });
          }else {
            console.log("no response")
          }
        });

        console.log(userInfoArray);
}

function componentDidMount() { // Runs after the first render
  Axios.get('http://localhost:3001/checklogin').then((response)=> {
  if(response.data.loggedIn === true){
    user = response.data.user[0].username
    this.getUserInfo(); // Fetch the users info and posts by calling function
  }
  });
}

const navbar = props => (
    <header className="navbar">
        <nav className="navbarContent">
            <div>
                <SideDrawerButton click={props.drawerClickedHandler}/>
            </div>
            <div className="navbarLogo"><a href=''>PicShare</a></div>
            <div className="spacer"></div>
            <div className="navbarItems">
                <ul>
                    <li><Link to="/allNewsFeed">News Feed</Link></li>
                    <li><Link to="/Explore">Explore</Link></li>
                    <li><Link to="/allNewsFeed">Upload</Link></li>
                    <div class="dropdown">
                        <button class="dropbtn">Tom Scott</button>
                        <div class="dropdown-content">
                            <a href="/profile">My Account</a>
                            <a href="/">Logout</a>
                            <a href="#">Privacy Policy</a>
                        </div>
                    </div>
                </ul>
            </div>
        </nav>

    </header>
);



export default navbar;
