import List from '@material-ui/core/List';
import {withRouter, useHistory, BrowserRouter , Switch, Route, Link, Redirect} from "react-router-dom";
import PostDialog from './PostDialog';
import Post from './Post';


class Newsfeed extends Component {

    constructor(props){
        super(props);
        this.addPost = this.addPost.bind(this);
        this.createPost = this.createPost.bind(this);

        this.state = {
            feedItems : [],
            currentText : ""

        }
    }

    createPost(description, tags, file) {
        return <Post key={this.state.feedItems.length} description={description} tags={tags} file={file}/>;
