import React, { Component } from 'react';

import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import ListItem from '@material-ui/core/ListItem';
import CardContent from '@material-ui/core/CardContent';
import Axios from 'axios';









class Post extends Component {
    constructor(props) {
      super(props);
      this.state = { description: props.description, key: props.key, file: props.file, tags : props.tags, user: props.user, add: true, split: [], id: ""};
      console.log("Props file: " + props.file);
    }



    postPostInfo() {
      console.log("inpostInfo")
      Axios.post('http://localhost:3001/posts', {
                username: this.state.user,
                description: this.state.description,
                img: this.state.file

            }).then((response) => {
              if(response){
                this.setState({
                  id: response
                  });
              }else {
                console.log("no response")
              }


            })
    this.setState({
      add: false
      });
    }

    postPostTags() {
      console.log
      Axios.post('http://localhost:3001/tags', {
                tags: this.state.split,
                id: this.state.id


            }).then((response) => {
              if(response){

              }else {
                console.log("no response")
              }


            })

    }

    parseTags(){
      var splitTags = this.state.tags.split(" ")
      this.setState({
        split: splitTags
        });
    }

    componentDidMount() { // Runs after the first render
      console.log("in component")
          this.postPostInfo();
          this.parseTags();
          this.postPostTags();





    }








    render() {
      return (

        <ListItem key={this.state.key}>
            <Card>
                <CardContent>
                <img src={this.state.file} />
                <Typography>
                    User
                </Typography>
                <Typography color="textSecondary" gutterBottom>
                    {this.state.description}
                </Typography>
                <br></br>
                <Typography color="textSecondary" gutterBottom>
                    {this.state.tags}
                </Typography>
                </CardContent>
            </Card>

        </ListItem>
      );
    }
}

export default Post;
