import React, { Component } from 'react';

import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import ListItem from '@material-ui/core/ListItem';
import CardContent from '@material-ui/core/CardContent';









class Post extends Component {
    constructor(props) {
      super(props);
      this.state = { description: props.description, key: props.key, file: props.file, tags : props.tags, user: props.user };
      console.log("Props file: " + props.file);
    }



    postPostInfo() {
      console.log("inpostInfo")
      Axios.post('http://localhost:3001/posts', {
                username: user,
                description: this.state.description,
                img: this.state.file

            }).then((response) => {
              if(response){
                this.setState({
                  usersInfoArray: response.data, // Store the information in the users info array
                });
              }else {
                console.log("no response")
              }


            })
    }

    componentDidMount() { // Runs after the first render
        Axios.get('http://localhost:3001/checklogin').then((response)=> {
        if(response.data.loggedIn === true){
          user = response.data.user[0].Username
          console.log(response.data.user[0].Username)
          this.postPostInfo(); // Fetch the users info by calling function

        }

        });

    }








    render() {
      return (
        <ListItem key={this.state.key}>
            <Card>
                <CardContent>
                <img src={this.state.file} />
                <Typography>
                    User
                </Typography>
                <Typography color="textSecondary" gutterBottom>
                    {this.state.description}
                </Typography>
                <br></br>
                <Typography color="textSecondary" gutterBottom>
                    {this.state.tags}
                </Typography>
                </CardContent>
            </Card>

        </ListItem>
      );
    }
}

export default Post;
